mapper.select:
	SELECT b.bookid
	  FROM book b 
     WHERE b.bookid = #{bookId}

mapper.insert:
INSERT INTO book b (b.bookid
				  , b.title
				  , b.author
				  , b.price
				  , b.isbn
				  , b.publish)
VALUES (#{bookId}
	  , #{title}
	  , #{author}
	  , #{price}
	  , #{isbn}
	  , #{publish})

mapper.update:
		UPDATE book b
		
			b.moddate = sysdate
			
				, b.title = #{title}
			
			
				, b.author = #{author}
			

			
				, b.price = #{price}
			

			
				, b.isbn = #{isbn}
			

			
				, b.publish =  #{publish}
			
			
			WHERE b.BOOKID = #{bookId}
